import React, { Component } from 'react';
import axios from 'axios';

import logo from './logo.png';
import SerieList from './components/SerieList';
import { AuthDialog } from './components/Dialog';
import { withCookies } from 'react-cookie';

const url = 'https://searchasdf.search.windows.net/indexes/azuresql-index/docs?api-version=2017-11-11&api-key=9CF28743EE2C4D83F8320E360423DEF5&search=*';

class App extends Component {
  state = {
    series: [],
    authOpen: false,
    apiKey: null
  };

  openAuth = () => this.setState({ authOpen: true });
  closeAuth = () => this.setState({ authOpen: false });
  saveAuth = (apiKey) => {
    Cookies.set('apiKey', apiKey, { maxAge: 500, sameSite: true });
    this.closeAuth();
  }

  componentDidMount() {
    this.setState({apiKey: Cookies.get('apiKey')});
    if (!this.state.apiKey) {
      this.openAuth();
    }
    else {
      axios.get(url)
        .then(response => {
          this.setState({
            series: response.data.value.map(x => {
              return {
                name: x.Name,
                score: x.Rating,
                numberOfTweets: x.VotesCount
              };
            })
          });
        })
        .catch(error => {
          console.log(error);
        });
    }
  }

  render() {
    return (
      <div className='App'>
        <header className='App-header'>
          <img src={logo} className='App-logo' alt='logo' />
        </header>
        <AuthDialog open={this.state.authOpen} handleClose={this.closeAuth} />
        <SerieList listOfSeries={this.state.series} />
      </div>
    );
  }
}

export default withCookies(App);
