import React, { Component } from 'react';
import axios from 'axios';

import logo from './logo.png';
import SerieList from './components/SerieList';
import { AuthDialog } from './components/Dialog';
import { withCookies } from 'react-cookie';

const url = 'https://searchasdf.search.windows.net/indexes/azuresql-index/docs?api-version=2017-11-11';

class App extends Component {
  state = {
    series: [],
    authOpen: false,
    apiKey: null
  };

  openAuth = () => this.setState({ authOpen: true });
  closeAuth = () => this.setState({ authOpen: false });
  saveAuth = () => {
    this.props.cookies.set('apiKey', this.state.apiKey, { maxAge: 500, sameSite: true });
    this.closeAuth();
  }
  handleChange = value => this.setState({apiKey: value});
  fetchSeries = () => {
    axios.get(`${url}&api-key=${this.props.cookies.get('apiKey')}&search=*'`)
        .then(response => {
          this.setState({
            series: response.data.value.map(x => {
              return {
                name: x.Name,
                score: x.Rating,
                numberOfTweets: x.VotesCount
              };
            })
          });
        })
        .catch(error => {
          this.props.cookies.remove('apiKey');
        });
  }

  componentDidMount() {
    if (!this.props.cookies.get('apiKey')) {
      this.openAuth();
    }
    else {
      this.fetchSeries();
    }
  }

  render() {
    return (
      <div className='App'>
        <header className='App-header'>
          <img src={logo} className='App-logo' alt='logo' />
        </header>
        <AuthDialog open={this.state.authOpen} handleClose={this.closeAuth} handleSave={this.saveAuth} handleChange={this.handleChange} />
        <SerieList listOfSeries={this.state.series} />
      </div>
    );
  }
}

export default withCookies(App);
