import axios from 'axios';

export default seriesApiClient = (url, apiKey, onError) => {
    return axios.get(`${url}&api-key=${apiKey}&search=*`)
        .then(response => {
            this.setState({
                series: response.data.value.map(x => {
                    return {
                        name: x.Name,
                        score: x.Rating,
                        numberOfTweets: x.VotesCount
                    };
                })
            });
        })
        .catch(error => {
            onError();
        });
}
